import React, {Component} from 'react';
import {StyleSheet,Text,View,SectionList,} from 'react-native';


const CITY_NAMES = [
    {title:'nuestros productos',data:['Camisas','Jockey','Micropolar','Pantalones','Cargo','Poleras','Reflectantes']},

]

export default class FlatListDemo extends Component<Props> {
    static navigationOptions = {
        title: 'SectionList',
    };


    render() {
        return (
            <View style={styles.container}>
                <SectionList
                    // 1 Adquisición y renderizado de datos
                    sections={CITY_NAMES}   // Fuente de datos de la lista de categorías
                    renderItem={(data) =><View style={styles.item}>
                                              <Text onPress={()=> someAction()} style={styles.text}>{data.item}</Text>
                                         </View>}


                    // 2 Renderizar el título de la categoría (deconstruir la sección de la fuente de datos)
                    renderSectionHeader={({section})=><View style={styles.SectionListHeader}>
                                                        <Text style={styles.text}>{section.title}</Text>
                                                   </View>}
                    ItemSeparatorComponent={()=>< View  style={styles.separator}/>}//linea divisoria 
                />
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
    },
    item: {
        backgroundColor: '#174da0',
        height: 100,
        marginRight: 15,
        marginLeft: 15,
        alignItems: 'center',
        justifyContent: 'center',
        elevation:5,// El efecto de flotar
        borderRadius:5,//Esquinas redondeadas
    },
    // Estilo de encabezado
    SectionListHeader:{
        height:60,
        alignItems:'center',
        justifyContent:'center',
        backgroundColor:'#9ea1ff',
        marginRight: 15,
        marginLeft: 15,
        borderRadius:5,//Esquinas redondeadas
    },
    text: {
        color: '#401d14',
        fontSize: 20,
    },
    // Estilo de línea divisoria
    separator:{
        height:1,
        backgroundColor:'gray',
        marginRight: 15,
        marginLeft: 15,
    }
});
